import React, { useState } from 'react';
import {useDropzone} from 'react-dropzone';
import axios from "axios";
import { Button } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUpload } from '@fortawesome/free-solid-svg-icons'

export const BasicUpload = () => {
  const [errorLogin, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [processedOrders, setProcessedOrders] = useState([]);

  const {acceptedFiles, getRootProps, getInputProps} = useDropzone({ multiple: false });
  
  const files = acceptedFiles.map(file => (
    <li key={file.path}>
      {file.path} - {file.size} bytes
    </li>
  ));

  let formData = new FormData()
  
  acceptedFiles.map(file => {
    console.log(file)
    formData.append("formFile", file);
    formData.append("fileName", file.name);
  })
  
  const uploadFile = async (e) => {
    try {
      const res = await axios.post("https://localhost:44387/api/xml/ProcessXmlFile", formData)
      .then(function (response) {
        setProcessedOrders(response.data);
        setIsLoading(false);
      })
      .catch(function(error) {
        if (!error.status) {
            try{
                const {status} = error.response;
                if(status == 401)
                     error.message = 'Unauthorized access! Please contact the Administrator!'
            }
            catch{
                setError(error);
                setIsLoading(false);
            }
            finally{
                setError(error);
                setIsLoading(false);
            }
        }
        else{
            
        }
      });
      console.log(res);
    } catch (ex) {
      console.log(ex);
    }
  };

  return (
    <section className="container">
      <div {...getRootProps({className: 'dropzone'})}>
        <input {...getInputProps()} />
        <p>Drag 'n' drop Order xml file here, or click to select file</p>
      </div>
      <aside>
        <h4>File selected:</h4>
        <ul>{files}</ul>
      </aside>
      <Button variant="warning" onClick={uploadFile}> 
        <FontAwesomeIcon icon={faUpload} />
        &nbsp;
        UploadFile
      </Button>
      { processedOrders.length?
        processedOrders.map(orders => (
          <div className="row">
            <div className="col-lg-12 col-md-12 text-center custom-div-template  use-pointer" onClick={() => console.log("da")}>
                  <h4>
                      Customer Name: {orders.customerName} <br/>
                      Customer Email: {orders.customerEmail} <br/>
                      Date Required: {orders.dateRequired} <br/>
                      Notes: {orders.notes} <br/>
                      Quantity: {orders.quantity} <br/>  
                      Size: {orders.size} <br/>
                  </h4>
              </div>
          </div>
        )) : <></>
      }
    </section>
  );
}
