import React from 'react';
import {useDropzone} from 'react-dropzone';
import axios from "axios";
import { Button } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBolt, faWrench, faShieldAlt, faTrashAlt, faUserSecret, faGlobe } from '@fortawesome/free-solid-svg-icons'

export const BasicUpload = () => {
  const {acceptedFiles, getRootProps, getInputProps} = useDropzone({ multiple: false });
  
  const files = acceptedFiles.map(file => (
    <li key={file.path}>
      {file.path} - {file.size} bytes
    </li>
  ));

  let formData = new FormData()
  
  acceptedFiles.map(file => {
    console.log(file)
    formData.append("formFile", file);
    formData.append("fileName", file.name);
  })
  
  const uploadFile = async (e) => {
    try {
      const res = await axios.post("https://localhost:44387/api/xml/ProcessXmlFile", formData);
      console.log(res);
    } catch (ex) {
      console.log(ex);
    }
  };
  

  return (
    <section className="container">
      <div {...getRootProps({className: 'dropzone'})}>
        <input {...getInputProps()} />
        <p>Drag 'n' drop Order xml file here, or click to select file</p>
      </div>
      <aside>
        <h4>File selected:</h4>
        <ul>{files}</ul>
      </aside>
      <Button className="margin10" variant="secondary" onClick={uploadFile}> 
        <FontAwesomeIcon icon={faBolt} />
        &nbsp;
        UploadFile
      </Button>
      <input type="button" value="upload" onClick={uploadFile} />
    </section>
  );
}
