import React, { useState } from 'react';
import {useDropzone} from 'react-dropzone';
import axios from "axios";
import { Button } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUpload } from '@fortawesome/free-solid-svg-icons'

export const BasicUpload = () => {
  const [errorLogin, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const {acceptedFiles, getRootProps, getInputProps} = useDropzone({ multiple: false });
  
  const files = acceptedFiles.map(file => (
    <li key={file.path}>
      {file.path} - {file.size} bytes
    </li>
  ));

  let formData = new FormData()
  
  acceptedFiles.map(file => {
    console.log(file)
    formData.append("formFile", file);
    formData.append("fileName", file.name);
  })
  
  const uploadFile = async (e) => {
    try {
      const res = await axios.post("https://localhost:44387/api/xml/ProcessXmlFile", formData)
      .then(function (response) {
        setIsLoading(false);
      })
      .catch(function(error) {
        if (!error.status) {
            try{
                const {status} = error.response;
                if(status == 401)
                     error.message = 'Unauthorized access! Please contact the Administrator!'
            }
            catch{
                setError(error);
                setIsLoading(false);
            }
            finally{
                setError(error);
                setIsLoading(false);
            }
        }
        else{
            
        }
      });
      console.log(res);
    } catch (ex) {
      console.log(ex);
    }
  };
  
  const processFile = async (e) => {
    if(!isLoading) {
        setIsLoading(true);
        try {
            const result = await axios("https://localhost:44387/api/xml/ProcessXmlFile", formData)
            .then(function (response) {
                setIsLoading(false);
            })
            .catch(function(error) {
                if (!error.status) {
                    try{
                        const {status} = error.response;
                        if(status == 401)
                             error.message = 'Unauthorized access! Please contact the Administrator!'
                    }
                    catch{
                        setError(error);
                        setIsLoading(false);
                    }
                    finally{
                        setError(error);
                        setIsLoading(false);
                    }
                }
                else{
                    
                }
            });
            
        } catch (e) {
            setError(e);
            setIsLoading(false);
            console.log(e);
        }
    }
  };

  return (
    <section className="container">
      <div {...getRootProps({className: 'dropzone'})}>
        <input {...getInputProps()} />
        <p>Drag 'n' drop Order xml file here, or click to select file</p>
      </div>
      <aside>
        <h4>File selected:</h4>
        <ul>{files}</ul>
      </aside>
      <Button variant="warning" onClick={uploadFile}> 
        <FontAwesomeIcon icon={faUpload} />
        &nbsp;
        UploadFile
      </Button>
    </section>
  );
}
